import React, { useState } from 'react';
import Ckeditor from '../component/Ckeditor';
import styled from 'styled-components';

const FormContainer = styled.section`
  margin: 40px;
  border: 1 px solid;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;
const ContactForm = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  row-gap: 20px;
`;
const Button = styled.button`
  padding: 5px 10px;
  background-color: blue;
  border: none;
  border-radius: 5px;
  height: 30px;
  width: 80px;
  color: white;
`;
const InputC = styled.section`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  row-gap: 20px;
`;
const Input = styled.input`
  height: 30px;
  width: 400px;
`;
const Form = () => {
  const [formData, setFormData] = useState({
    title: '',
    email: '',
    phone: '',
    content: '',
  });
  const { title, email, phone, content } = formData;
  const onChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };
  return (
    <FormContainer>
      <h1>CONTACT FORM</h1>
      <ContactForm>
        <InputC>
          <label>Title</label>
          <Input
            onChange={onChange}
            type="text"
            name="text"
            placeholder="Enter title"
          />
        </InputC>
        <InputC>
          <label>Email</label>
          <Input
            onChange={onChange}
            type="email"
            name="email"
            placeholder="Enter email"
          />
        </InputC>
        <InputC>
          <label>PhoneNumber</label>
          <Input
            onChange={onChange}
            type="string"
            name="phone"
            placeholder="Enter phone number"
          />
        </InputC>
        <label>Content</label>

        <Ckeditor />
        <InputC>
          <Button type="submit">Submit</Button>
        </InputC>
      </ContactForm>
    </FormContainer>
  );
};

export default Form;
